class RedBean_Can implements Iterator , ArrayAccess , SeekableIterator , Countable { private $collectionIDs = null; private $type = null; private $pointer = 0; private $num = 0; public function __construct( $type="", $collection = array() ) { $this->collectionIDs = $collection; $this->type = $type; $this->num = count( $this->collectionIDs ); } public function wrap( $bean ) { $dclass = PRFX.$this->type.SFFX; $deco = new $dclass( floatval( $bean->id ) ); $deco->setData( $bean ); return $deco; } public function count() { return $this->num; } public function getBeans() { $rows = RedBean_OODB::fastloader( $this->type, $this->collectionIDs ); $beans = array(); if (is_array($rows)) { foreach( $rows as $row ) { $beans[] = $this->wrap( RedBean_OODB::getById( $this->type, $row["id"] , $row) ); } } return $beans; } public function slice( $begin=0, $end=0 ) { $this->collectionIDs = array_slice( $this->collectionIDs, $begin, $end); $this->num = count( $this->collectionIDs ); } public function current() { if (isset($this->collectionIDs[$this->pointer])) { $id = $this->collectionIDs[$this->pointer]; return $this->wrap( RedBean_OODB::getById( $this->type, $id ) ); } else { return null; } } public function key() { return $this->pointer; } public function next() { return ++$this->pointer; } public function prev() { if ($this->pointer > 0) { return ++$this->pointer; }else { return 0; } } public function rewind() { $this->pointer=0; return 0; } public function seek( $seek ) { $this->pointer = (int) $seek; } public function valid() { return ($this->num > ($this->pointer+1)); } public function hasMoreBeans() { return $this->valid(); } public function offsetSet($offset, $value) { $this->collectionIDs[$offset] = $value; } public function offsetExists($offset) { return isset($this->collectionIDs[$offset]); } public function offsetUnset($offset) { unset($this->collectionIDs[$offset]); } public function offsetGet($offset) { if (isset($this->collectionIDs[$offset])) { $id = $this->collectionIDs[$offset]; return $this->wrap( RedBean_OODB::getById( $this->type, $id ) ); } else { return null; } } public function getList() { $list = array(); $beans = $this->getBeans(); foreach($beans as $bean) { $list[] = $bean->exportAsArr(); } return $list; } public function reverse() { $this->collectionIDs = array_reverse($this->collectionIDs, true); return $this; } }
class RedBean_DBAdapter extends RedBean_Observable { private $db = null; private $sql = ""; public function __construct($database) { $this->db = $database; } public function getSQL() { return $this->sql; } public function escape( $sqlvalue ) { return $this->db->Escape($sqlvalue); } public function exec( $sql , $noevent=false) { if (!$noevent){ $this->sql = $sql; $this->signal("sql_exec", $this); } return $this->db->Execute( $sql ); } public function get( $sql ) { $this->sql = $sql; $this->signal("sql_exec", $this); return $this->db->GetAll( $sql ); } public function getRow( $sql ) { $this->sql = $sql; $this->signal("sql_exec", $this); return $this->db->GetRow( $sql ); } public function getCol( $sql ) { $this->sql = $sql; $this->signal("sql_exec", $this); return $this->db->GetCol( $sql ); } public function getCell( $sql ) { $this->sql = $sql; $this->signal("sql_exec", $this); $arr = $this->db->GetCol( $sql ); if ($arr && is_array($arr)) return ($arr[0]); else return false; } public function getInsertID() { return $this->db->getInsertID(); } public function getAffectedRows() { return $this->db->Affected_Rows(); } public function getDatabase() { return $this->db; } public function getErrorMsg() { return $this->db->Errormsg(); } }
class RedBean_Decorator extends RedBean_Observable implements IteratorAggregate { protected $data = null; protected $type = ""; protected $problems = array(); public function __construct( $type=false, $id=0, $lock=false ) { $id = floatval( $id ); if (!$type) { throw new Exception("Undefined bean type"); } else { $this->type = preg_replace( "[\W_]","", strtolower($type)); if ($id > 0) { $this->data = RedBean_OODB::getById( $this->type, $id, $lock ); } else { $this->data = RedBean_OODB::dispense( $this->type ); } } } public function free( $property ) { $this->signal("deco_free", $this); RedBean_OODB::dropColumn( $this->type, $property ); } public function importFromPost( $selection=null ) { $this->signal("deco_importpost", $this); if (!$selection) { $selection = array_keys($_POST); } if (is_string($selection)) { $selection = explode(",",$selection); } if ($selection && is_array($selection) && count($selection) > 0) { foreach( $selection as $field ) { $setter = "set".ucfirst( $field ); if (isset( $_POST[$field] )) { $resp = $this->$setter( $_POST[ $field ] ); } } } return $this; } public function import( $arr ) { $this->signal("deco_import", $this); foreach( $arr as $key=>$val ) { $setter = "set".ucfirst( $key ); $resp = $this->$setter( $val ); } return $this; } public function __call( $method, $arguments ) { return $this->command( $method, $arguments ); } public function __get( $name ) { $this->signal("deco_get", $this); return isset($this->data->$name) ? $this->data->$name : null; } public function __set( $name, $value ) { $this->signal("deco_set", $this); $this->data->$name = $value; } public function command( $method, $arguments ) { if (strpos( $method,"set" ) === 0) { $prop = substr( $method, 3 ); $this->$prop = $arguments[0]; return $this; } elseif (strpos($method,"getRelated")===0) { $this->signal("deco_get", $this); $prop = strtolower( substr( $method, 10 ) ); $beans = RedBean_OODB::getAssoc( $this->data, $prop ); $decos = array(); $dclass = PRFX.$prop.SFFX; if ($beans && is_array($beans)) { foreach($beans as $b) { $d = new $dclass(); $d->setData( $b ); $decos[] = $d; } } return $decos; } elseif (strpos( $method, "get" ) === 0) { $prop = substr( $method, 3 ); return $this->$prop; } elseif (strpos( $method, "is" ) === 0) { $prop = strtolower( substr( $method, 2 ) ); if (!isset($this->data->$prop)) { $this->signal("deco_get",$this); return false; } return ($this->data->$prop ? TRUE : FALSE); } else if (strpos($method,"add") === 0) { $this->signal("deco_add",$this); $deco = $arguments[0]; $bean = $deco->getData(); RedBean_OODB::associate($this->data, $bean); return $this; } else if (strpos($method,"remove")===0) { $this->signal("deco_remove",$this); $deco = $arguments[0]; $bean = $deco->getData(); RedBean_OODB::unassociate($this->data, $bean); return $this; } else if (strpos($method,"attach")===0) { $this->signal("deco_attach",$this); $deco = $arguments[0]; $bean = $deco->getData(); RedBean_OODB::addChild($this->data, $bean); return $this; } else if (strpos($method,"clearRelated")===0) { $this->signal("deco_clearrelated",$this); $type = strtolower( substr( $method, 12 ) ); RedBean_OODB::deleteAllAssocType($type, $this->data); return $this; } else if (strpos($method,"numof")===0) { $this->signal("deco_numof",$this); $type = strtolower( substr( $method, 5 ) ); return RedBean_OODB::numOfRelated($type, $this->data); } } public function belongsTo( $deco ) { $this->signal("deco_belongsto", $this); RedBean_OODB::deleteAllAssocType($deco->getType(), $this->data); RedBean_OODB::associate($this->data, $deco->getData()); } public function exclusiveAdd( $deco ) { $this->signal("deco_exclusiveadd", $this); RedBean_OODB::deleteAllAssocType($this->type,$deco->getData()); RedBean_OODB::associate($deco->getData(), $this->data); } public function parent() { $this->signal("deco_parent", $this); $beans = RedBean_OODB::getParent( $this->data ); if (count($beans) > 0 ) $bean = array_pop($beans); else return null; $dclass = PRFX.$this->type.SFFX; $deco = new $dclass(); $deco->setData( $bean ); return $deco; } public function siblings() { $this->signal("deco_siblings", $this); $beans = RedBean_OODB::getParent( $this->data ); if (count($beans) > 0 ) { $bean = array_pop($beans); } else { return null; } $beans = RedBean_OODB::getChildren( $bean ); $decos = array(); $dclass = PRFX.$this->type.SFFX; if ($beans && is_array($beans)) { foreach($beans as $b) { if ($b->id != $this->data->id) { $d = new $dclass(); $d->setData( $b ); $decos[] = $d; } } } return $decos; } public function children() { $this->signal("deco_children", $this); $beans = RedBean_OODB::getChildren( $this->data ); $decos = array(); $dclass = PRFX.$this->type.SFFX; if ($beans && is_array($beans)) { foreach($beans as $b) { $d = new $dclass(); $d->setData( $b ); $decos[] = $d; } } return $decos; } public function hasParent( $deco ) { $me = $this; while( $parent = $me->parent() ) { if ($deco->getID() == $parent->getID()) { return true; } else { $me = $parent; } } return false; } public function hasChild( $deco ) { $nodes = array($this); while($node = array_shift($nodes)) { if ($node->getID() == $deco->getID() && ($node->getID() != $this->getID())) { return true; } if ($children = $node->children()) { $nodes = array_merge($nodes, $children); } } return false; } public function hasSibling( $deco ) { $siblings = $this->siblings(); foreach( $siblings as $sibling ) { if ($sibling->getID() == $deco->getID()) { return true; } } return false; } public function copy() { $this->signal("deco_copy", $this); $clone = new self( $this->type, 0 ); $clone->setData( $this->getData() ); return $clone; } public function clearAllRelations() { $this->signal("deco_clearrelations", $this); RedBean_OODB::deleteAllAssoc( $this->getData() ); } public function getData() { return $this->data; } public function setData( $data ) { $this->data = $data; } public function save() { $this->signal("deco_save", $this); return RedBean_OODB::set( $this->data ); } public static function delete( $deco ) { RedBean_OODB::trash( $deco->getData() ); } public function lock() { RedBean_OODB::openBean($this->getData()); } public function unlock() { RedBean_OODB::closeBean( $this->getData()); } public static function close( $deco ) { RedBean_OODB::closeBean( $deco->getData() ); } public static function make( $type="", $id ){ return new RedBean_Decorator( $type, $id ); } public function exportTo( &$bean, $overridebean=false ) { foreach($this->data as $prop=>$value) { if (is_object($overridebean) && isset($overridebean->$prop)) { $value = $overridebean->$prop; } elseif (is_array($overridebean) && isset($overridebean[$prop])) { $value = $overridebean[$prop]; } if (is_object($value)){ $value = $value->getID(); } if (is_object($bean)) { $bean->$prop = $value; } elseif (is_array($bean)) { $bean[$prop] = $value; } } return $bean; } public function exportAsArr() { $arr = array(); foreach($this->data as $prop=>$value) { if ($value instanceof RedBean_Decorator){ $value = $value->getID(); } $arr[ $prop ] = $value; } return $arr; } public static function find( $deco, $filter, $start=0, $end=100, $orderby=" id ASC ", $extraSQL=false ) { if (!is_array($filter)) { return array(); } if (count($filter)<1) { return array(); } $filters = array(); foreach($filter as $key=>$f) { $filters[strtolower($key)] =$f; if (!in_array($f,array("=","!=","<",">","<=",">=","like","LIKE"))) { throw new ExceptionInvalidFindOperator(); } } $beans = RedBean_OODB::find( $deco->getData(), $filters, $start, $end, $orderby, $extraSQL ); $decos = array(); $dclass = PRFX.$deco->type.SFFX; foreach( $beans as $bean ) { $decos[ $bean->id ] = new $dclass( floatval( $bean->id ) ); $decos[ $bean->id ]->setData( $bean ); } return $decos; } public function getIterator() { $o = new ArrayObject($this->data); return $o->getIterator(); } public function isReadOnly() { try{ RedBean_OODB::openBean($this->data, true); } catch(RedBean_Exception_FailedAccessBean $e){ return false; } return true; } }
class Redbean_Driver_PDO implements RedBean_Driver { private static $instance; private $debug = false; private $pdo; private $affected_rows; private $rs; private $exc =0; public static function getInstance($dsn, $user, $pass, $dbname) { if(is_null(self::$instance)) { self::$instance = new Redbean_Driver_PDO($dsn, $user, $pass); } return self::$instance; } public function __construct($dsn, $user, $pass) { $this->pdo = new PDO( $dsn, $user, $pass, array(PDO::MYSQL_ATTR_INIT_COMMAND => 'SET NAMES utf8', PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION, PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC) ); } public function GetAll( $sql ) { $this->exc = 0; try{ if ($this->debug) { echo "<HR>" . $sql; } $rs = $this->pdo->query($sql); $this->rs = $rs; $rows = $rs->fetchAll(); if(!$rows) { $rows = array(); } if ($this->debug) { if (count($rows) > 0) { echo "<br><b style='color:green'>resultset: " . count($rows) . " rows</b>"; } } } catch(Exception $e){ $this->exc = 1; if ($this->debug){ $str = $this->Errormsg(); if ($str != "") { echo "<br><b style='color:red'>" . $str . "</b>"; } } return array(); } return $rows; } public function GetCol($sql) { $this->exc = 0; try{ $rows = $this->GetAll($sql); $cols = array(); if ($rows && is_array($rows) && count($rows)>0){ foreach ($rows as $row) { $cols[] = array_shift($row); } } } catch(Exception $e){ $this->exc = 1; return array(); } return $cols; } public function GetCell($sql) { $this->exc = 0; try{ $arr = $this->GetAll($sql); $row1 = array_shift($arr); $col1 = array_shift($row1); } catch(Exception $e){ $this->exc = 1; } return $col1; } public function GetRow($sql) { $this->exc = 0; try{ $arr = $this->GetAll($sql); } catch(Exception $e){ $this->exc = 1; return array(); } return array_shift($arr); } public function ErrorNo() { if (!$this->exc) return 0; $infos = $this->pdo->errorInfo(); return $infos[1]; } public function Errormsg() { if (!$this->exc) return ""; $infos = $this->pdo->errorInfo(); return $infos[2]; } public function Execute( $sql ) { $this->exc = 0; try{ if ($this->debug) { echo "<HR>" . $sql; } $this->affected_rows = $this->pdo->exec($sql); } catch(Exception $e){ $this->exc = 1; if ($this->debug) { $str = $this->Errormsg(); if ($str != "") { echo "<br><b style='color:red'>" . $str . "</b>"; } } return 0; } return $this->affected_rows; } public function Escape( $str ) { return substr(substr($this->pdo->quote($str), 1), 0, -1); } public function GetInsertID() { return (int) $this->pdo->lastInsertId(); } public function Affected_Rows() { return (int) $this->affected_rows; } public function setDebugMode( $tf ) { $this->debug = (bool)$tf; } public function GetRaw() { return $this->rs; } }
interface RedBean_Driver { public static function getInstance( $host, $user, $pass, $dbname ); public function GetAll( $sql ); public function GetCol( $sql ); public function GetCell( $sql ); public function GetRow( $sql ); public function ErrorNo(); public function Errormsg(); public function Execute( $sql ); public function Escape( $str ); public function GetInsertID(); public function Affected_Rows(); public function setDebugMode( $tf ); public function GetRaw(); }
